import React from "react";
import "./index.css";

export default function Header(props) {
  const { updateTodos } = props;
  const handleKeyUp = (e) => {
    if (e.keyCode === 13) {
      if (e.target.value.trim() !== "") {
        updateTodos({
          id: Date.now().toString(),
          name: e.target.value,
          done: false,
        });
        e.target.value = "";
      } else {
        alert("do something");
        e.target.value = "";
      }
    }
  };

  return (
    <div className="header">
      <div className="input">
        <input type="text" onKeyUp={handleKeyUp} />
      </div>
      <button>+..</button>
    </div>
  );
}
